	       A.FullName AS Author, PhysicalPath, W.Title, Summary, URL 
	  FROM Webcast W 
		LEFT OUTER JOIN Vendor      V ON W.VendorID=V.ID
		LEFT OUTER JOIN Author      A ON W.AuthorID=A.ID
		LEFT OUTER JOIN WebcastTag WT ON W.ID=WT.WebcastID
		LEFT OUTER JOIN Tag         T ON WT.TagID=T.ID
