    INSERT INTO dbo.Webcast(ID, PhysicalPath, Title, VendorID, AuthorID, ReleaseDate, Summary, URL) VALUES (797, 'PRIMARY Webcasts\A-P\ASP.NET-Core', 'ASP.NET MVC Core - Absolute Basics', 43, 63, '2017-10-19', 'In just three hours, you''ll have the perfect foundation for growing your expertise in ASP.NET MVC Core. This course is intended for those who have completed the C# via ASP.NET Course. You''ll learn the concepts, the major components of the framework, learn the workflow of building MVC apps, work with the Entity Framework Core and Visual Studio 2017 tooling to build an entire application. Bonus: a challenge (and solution) at the end of the course will help you exercise what you''ve learned.', '')
