    INSERT INTO dbo.Webcast(ID, PhysicalPath, Title, VendorID, AuthorID, ReleaseDate, Summary, URL) VALUES (1021, 'PRIMARY Webcasts\R-Z\Underscore', 'Underscore.js Array, Object, and Utility Functions', 19, 27, '2016-09-18', 'Underscore.js array functions take an array as input. Object functions work on objects, and utility functions provide utility, with or without input, or without being tied to a particular input type. This course covers a large range of array functions, object functions, and utility functions from the Underscore.js library.', '')
